export function calculateGoodDemand(tierCounts: Record<string, number>): Record<string, number> {
  const demand: Record<string, number> = {};
  Object.entries(tierCounts).forEach(([tier, count]) => {
    const entries = GOOD_CONSUMPTION[tier];
    if (!entries) return;
    entries.forEach(item => {
      const amount = (count / 1000) * item.tonsPer1000PerMinute;
      demand[item.good] = (demand[item.good] || 0) + amount;
    });
  });
  return demand;
}
